async function handleStripeWebhook(request, env, corsHeaders) {
  try {
    let rawBody = await request.text();
    // Verify webhook signature
    if (env.STRIPE_WEBHOOK_SECRET) {
      const signature = request.headers.get('stripe-signature');
      if (!signature) {
        throw new Error('Missing stripe-signature header');
      }
      const verified = await verifyStripeSignature(rawBody, signature, env.STRIPE_WEBHOOK_SECRET, 300);
      if (!verified.ok) {
        return new Response(
          JSON.stringify({ error: verified.error || 'Invalid Stripe signature' }),
          { status: 400, headers: corsHeaders }
        );
      }
    }

    const event = JSON.parse(rawBody || '{}');

    if (event.type === 'payment_intent.succeeded') {
      const pi = event.data.object;
      console.log('[Webhook] Payment intent succeeded');
      await maybeCreateOrderFromMetadata(pi, env);
    } else if (event.type === 'payment_intent.payment_failed') {
      console.log('[Webhook] Payment failed');
    } else {
      console.log('[Webhook] Unhandled event type:', event.type);
    }
    
    return new Response(
      JSON.stringify({ ok: true, received: true }),
      { headers: corsHeaders }
    );
  } catch (error) {
    return new Response(
      JSON.stringify({ error: error.message }), 
      { status: 400, headers: corsHeaders }
    );
  }
}

/**
 * Start live updates for charts and stats
 */
function startLiveUpdates() {
  liveUpdateInterval = setInterval(() => {
    // Update Hash Power chart
    if (hashPowerChart && gameState.history.hashRate.length > 0) {
      const latestData = gameState.history.hashRate.map(point => ({
        x: point.x,
        y: point.y
      }));
      hashPowerChart.updateSeries([{
        data: latestData
      }]);
    }

    // Update Production Rate chart
    if (productionRateChart && gameState.history.cryptoRate.length > 0) {
      const latestCryptoData = gameState.history.cryptoRate.map(point => ({
        x: point.x,
        y: point.y
      }));
      productionRateChart.updateSeries([{
        data: latestCryptoData
      }]);
    }

    // Update Crypto Balance chart
    if (cryptoBalanceChart && gameState.history.cryptoBalance.length > 0) {
      const latestBalanceData = gameState.history.cryptoBalance.map(point => ({
        x: point.x,
        y: point.y
      }));
      cryptoBalanceChart.updateSeries([{
        data: latestBalanceData
      }]);
    }

    // Update live stats display
    const hashRate = calculateHashRate(gameState);
    const difficulty = calculateDifficulty(gameState);
    const cryptoPerSec = calculateCyptoPerSecond(hashRate, difficulty);

    const liveBalanceEl = document.getElementById('liveBalance');
    const liveRateEl = document.getElementById('liveRate');
    const liveHashEl = document.getElementById('liveHash');

    if (liveBalanceEl) liveBalanceEl.textContent = formatNumber(gameState.crypto);
    if (liveRateEl) liveRateEl.textContent = formatNumber(cryptoPerSec, 6);
    if (liveHashEl) liveHashEl.textContent = formatHashRate(hashRate);
  }, 1000); // Update every second
}

/**
 * Stop live updates
 */
function stopLiveUpdates() {
  if (liveUpdateInterval) {
    clearInterval(liveUpdateInterval);
    liveUpdateInterval = null;
  }
}

namespace ContosoUniversity.Controllers
{
    public class HomeController : Controller
    {
        private readonly ILogger<HomeController> _logger;
        private readonly SchoolContext _context;

        public HomeController(ILogger<HomeController> logger, SchoolContext context)
        {
            _logger = logger;
            _context = context;
        }

        public IActionResult Index()
        {
            return View();
        }

        public IActionResult Privacy()
        {
            return View();
        }

        public async Task<ActionResult> About()
        {
            List<EnrollmentDateGroup> groups = new List<EnrollmentDateGroup>();
            var conn = _context.Database.GetDbConnection();
            try
            {
                await conn.OpenAsync();
                using (var command = conn.CreateCommand())
                {
                    string query = "SELECT EnrollmentDate, COUNT(*) AS StudentCount "
                        + "FROM Person "
                        + "WHERE Discriminator = 'Student' "
                        + "GROUP BY EnrollmentDate";
                    command.CommandText = query;
                    DbDataReader reader = await command.ExecuteReaderAsync();

                    if (reader.HasRows)
                    {
                        while (await reader.ReadAsync())
                        {
                            var row = new EnrollmentDateGroup { EnrollmentDate = reader.GetDateTime(0), StudentCount = reader.GetInt32(1) };
                            groups.Add(row);
                        }
                    }
                    reader.Dispose();
                }
            }
            finally
            {
                conn.Close();
            }
            return View(groups);
        }

        [ResponseCache(Duration = 0, Location = ResponseCacheLocation.None, NoStore = true)]
        public IActionResult Error()
        {
            return View(new ErrorViewModel { RequestId = Activity.Current?.Id ?? HttpContext.TraceIdentifier });
        }
    }
}
